mapper.update:
UPDATE group_info
group_name = #{groupName}, group_owner_id = #{groupOwnerId}, create_time = #{createTime}, group_notice = #{groupNotice}, join_type = #{joinType}, status = #{status}, img_path = #{imgPath},
WHERE group_id = #{groupId}

UPDATE group_info
SET group_notice = #{groupNotice}
WHERE group_id = #{groupId}
AND `status` = 0

UPDATE group_info
SET status = #{status}
WHERE group_id = #{groupId}

UPDATE group_info
SET img_path = #{imgPath}
WHERE group_id = #{groupId}
